#!/usr/bin/env python3
#define variable
dna = ''
ecoRIsite_start = dna.find('GAATTC')+1
ecoRIsite_end = ecoRIsite_start+6
print(f'EcoRI startPos:{ecoRIsite_start} endPos:{ecoRIsite_end}')

#translate into complement
makecomplement = dna.maketrans({'A':'T', 'T':'A', 'G':'C','C':'G'})
complement = dna.translate(makecomplement)
#reverse
reverse_complement = complement[::-1]

#find EcoRI site in reverse complement
ecoRIsite_start_comp = reverse_complement.find('GAATTC')+1
ecoRIsite_end_comp = ecoRIsite_start_comp+6
print(f'EcoRI comp startPos:{ecoRIsite_start_comp} comp endPos:{ecoRIsite_end_comp}')