#calculate GC content
#define variable
dna_gc = ''
#count each nucleotide
number_of_As = dna_gc.count('A')
number_of_Ts = dna_gc.count('T')
number_of_Gs = dna_gc.count('G')
number_of_Cs = dna_gc.count('C')
#add As and Ts, Gs and Cs
gc = number_of_Gs + number_of_Cs
at = number_of_As + number_of_Ts
# calc gc content
gc_content = (gc/(at+gc))
at_content = (at/(at+gc))

